    'province': `${e.target.province.value}`
    })
  }).then((resp) => resp.json())

}

  return(
    <form className='form_container' onSubmit={sumbitInfo}> 
      <input type="text" name="firstName" id="firstName" placeholder="FIRST NAME" />
