    headers: {
      "Content-Type": "application/x-www-form-urlencoded",
      "Access-Control-Allow-Origin": "*",
    },
    body: JSON.stringify({
    'visited': false,
    })
  }).then((resp) => resp.json())
  .then((respr)=>{
    console.log(JSON.stringify(respr))
  })
}
